 SELECT p.id AS 'Matricula',CONCAT_WS(' ',p.nombre,p.apellido1,p.Apellido2) AS 'Alumna', g.id AS 'Grado', g.nombre AS 'Grado Titulo' FROM grado g LEFT JOIN asignatura a ON a.id_grado = g.id JOIN alumno_se_matricula_asignatura m ON m.id_asignatura = a.id JOIN persona p ON p.id = m.id_alumno WHERE (p.sexo = 'M'  AND p.tipo = 'alumno');
 SELECT a.id AS 'Matricula', a.nombre AS 'Asignatura' FROM asignatura a LEFT JOIN grado g ON (a.id = g.id) WHERE a.id_grado = 4;
 SELECT p.nombre AS 'Nombre', p.apellido1 AS 'Primer Apellido', p.apellido2 AS 'Segundo Apellido', d.nombre AS 'Materia' FROM persona p RIGHT JOIN profesor pro ON pro.id_profesor = p.id JOIN departamento d ON pro.id_departamento = d.id ORDER BY p.nombre , p.apellido1, apellido2 ASC;
 SELECT CONCAT_WS(' ',p.nombre, p.apellido1, p.apellido2) AS 'Nombre del alumno',a.nombre AS 'Asignatura', c.anyo_inicio AS 'Año de inicio', c.anyo_fin AS 'Año final' FROM curso_escolar c JOIN alumno_se_matricula_asignatura m ON m.id_curso_escolar = c.id JOIN asignatura a ON m.id_asignatura = a.id JOIN persona p ON m.id_alumno = p.id WHERE p.nif = '26902806M';
 SELECT d.id AS 'Matricula', d.nombre AS 'Departamento', p.id_profesor AS 'Matricula del profesor' FROM departamento d LEFT JOIN profesor p ON d.id = p.id_departamento JOIN asignatura a USING (id_profesor) JOIN grado g ON a.id_grado = g.id WHERE g.nombre = 'Grado en Ingeniería Informática (Plan 2015)' GROUP BY p.id_profesor;
 SELECT p.id AS 'Matricula', CONCAT_WS(' ',p.nombre,p.apellido1,p.Apellido2) AS 'Alumnos', c.anyo_inicio AS 'Año inicio', c.anyo_fin AS 'Año Fin' FROM curso_escolar c JOIN alumno_se_matricula_asignatura m ON c.id = m.id_curso_escolar JOIN persona p ON m.id_alumno = p.id WHERE c.id = 5 AND p.tipo = 'alumno' GROUP BY p.id;
 SELECT CONCAT_WS(' ',p.nombre, p.apellido1, p.apellido2) AS 'Nombre del profesor', d.nombre AS 'Departamentos' FROM persona p RIGHT JOIN profesor t ON p.id = t.id_profesor  JOIN departamento d ON d.id = t.id_departamento;
 SELECT CONCAT_WS(' ',p.nombre, p.apellido1, p.apellido2) AS 'Nombre del profesor', d.nombre AS 'Departamentos' FROM persona p RIGHT JOIN profesor t ON p.id = t.id_profesor  JOIN departamento d ON d.id = t.id_departamento WHERE t.id_departamento IS NULL;
 SELECT CONCAT_WS(' ',p.nombre, p.apellido1, p.apellido2) AS 'Nombre del profesor', d.nombre AS 'Departamentos' FROM persona p RIGHT JOIN profesor t ON p.id = t.id_profesor  JOIN departamento d ON d.id = t.id_departamento WHERE t.id_profesor IS NULL;
 SELECT CONCAT_WS(' ',p.nombre, p.apellido1, p.apellido2) AS 'Nombre del profesor' FROM persona p JOIN profesor t ON p.id = t.id_profesor LEFT JOIN asignatura a USING (id_profesor) WHERE a.id IS NULL;
 SELECT a.nombre AS 'Asignatura' FROM persona p JOIN profesor t ON p.id = t.id_profesor RIGHT JOIN asignatura a USING (id_profesor) WHERE a.id_profesor IS NULL;
 SELECT d.nombre AS 'Departamento', a.id AS 'Matricula asignatura', a.nombre AS 'Asignatura' FROM alumno_se_matricula_asignatura m RIGHT JOIN asignatura a ON a.id = m.id_asignatura  JOIN profesor p USING (id_profesor) JOIN departamento d ON d.id = p.id_departamento WHERE m.id_asignatura IS NULL;